import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import ApplyForm from './ApplyForm';

const API = 'http://localhost:3000'; // Change to your backend URL/port

function JobDetails() {
  const { id } = useParams();
  const [job, setJob] = useState(null);
  const [showApply, setShowApply] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchJob = async () => {
      try {
        const response = await axios.get(`${API}/jobs/${id}`);
        setJob(response.data);
      } catch (error) {
        setJob(null);
      }
    };
    fetchJob();
  }, [id]);

  if (!job) {
    return <div>Job not found. <button onClick={() => navigate('/jobs')}>Back to Jobs</button></div>;
  }

  return (
    <div className="job-details">
      <h2>{job.title}</h2>
      <p><strong>Company:</strong> {job.company}</p>
      <p><strong>Location:</strong> {job.location}</p>
      <p>{job.description}</p>
      {!showApply ? (
        <button onClick={() => setShowApply(true)}>Apply</button>
      ) : (
        <ApplyForm jobTitle={job.title} />
      )}
      <button onClick={() => navigate('/jobs')} className="back-btn">Back to Jobs</button>
    </div>
  );
}

export default JobDetails;
